import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../AuthContext';
import logo from '../Images/TenantClearanceLogo.png';
import { users } from '../users';
import mahaitlogo from '../../src/Images/Maha_IT_LogoB.png';

const Login: React.FC = () => {
  const { login } = useAuth();
  const navigate = useNavigate();
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");

  const handleLogin = () => {
    const user = users.find(
      (u) => u.username === username && u.password === password
    );

    if (user) {
      login();
      localStorage.setItem("isLoggedIn", "true");
      navigate("/", { replace: true });
    } else {
      setError("Invalid username or password");
    }
  };

  return (
   <>
     <div className="bg">
        <div className="loginmob">
    <h1 className='text-center m-0 pt-4'>महाराष्ट्र राज्य शेतकरी सहाय्य योजना</h1>
    <div className='auth-box border border-dark mt-5'>
        {/* <span  className="mb-4 d-flex">
          <img src={logo} alt="Police Logo" className="img-fluid login-logo logo" />
        </span> */}
      <h2 className='my-4'>Login</h2>
      <div className='mb-3'>
        <label className="form-label" htmlFor="name">User Name <span className="text-danger">*</span></label>
          <input
        type="text"
        className='form-control'
        placeholder="Username"
        value={username}
        onChange={(e) => setUsername(e.target.value)}
      />
      </div>
      <div className='mb-3'>
         <label className="form-label" htmlFor="Password">Password <span className="text-danger">*</span></label>
         <input
        type="password"
        className='form-control'
        placeholder="Password"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
      />
      </div>
      <div className="d-flex align-items-center justify-content-between">
          <div className="form-check m-0">
            <input className="form-check-input" type="checkbox" value="" id="rememberPassword"/>
            <label className="form-check-label" htmlFor="rememberPassword">Remember</label>
          </div>
          <a href="forgot-password.html" className="text-primary text-decoration-underline">Lost password?</a>
        </div>
    <div className='d-grid py-3 mt-3'>
         <button onClick={handleLogin} className='btn btn-lg btn-success'>Login</button>
    </div>
      
     
    </div>
    </div>
 <div className="text-center copyrightlogin px-3">
          Copyright © 2025. Design and Developed By MAHAIT.
          <a href="https://mahait.org/" target="_blank" rel="noopener noreferrer">
            <img src={mahaitlogo} alt="MAHAIT Logo" className="mahaitlogo" ></img>
          </a>
        </div>

    </div>
   </>
  );
};

export default Login;
