import React from 'react';
import logo from '../Images/TenantClearanceLogo.png';
import { useNavigate, useLocation } from 'react-router-dom';
import { useAuth } from '../AuthContext';

const Header: React.FC = () => {

  const { isLoggedIn, logout } = useAuth();
  const navigate = useNavigate();
  const location = useLocation();

  const handleLogout = () => {
    logout();
    localStorage.removeItem("isLoggedIn");
    navigate("/");
  };

  // Hide header if on login page regardless of login state
  if (location.pathname === "/login") {
    return null;
  }

  // Optionally hide if not logged in (if desired)
  // if (!isLoggedIn) {
  //   return null;
  // }
    return (
        <header className="headers mb-8 px-3">
       <div className="row align-items-center ">
      <div className="col-2 col-md-4 text-start">
        <img src={logo} alt="Police Logo" className='logo' />
      </div>
      <div className="col col-md-4  text-start">
       <h1 className="text-center logotext" >महाराष्ट्र राज्य शेतकरी सहाय्य योजना</h1>
      </div>
       <div className="col-2 col-md-4  text-end">
       <button onClick={handleLogout} className='btn btn-primary btn-sm'>
        Logout
      </button>
      </div>
    </div>
    </header>
    );
};

export default Header;
