/* eslint-disable jsx-a11y/anchor-is-valid */
/* eslint-disable react-hooks/exhaustive-deps */
import React, { useEffect, useState } from 'react';
import { useDispatch } from 'react-redux';
import { deleteFileUploadState } from '../../store/features/fileUploadSlice';
import { resetAllAuthentication } from '../../store/features/authenticationSlice';
import i18n from "i18next";
import { useTranslation } from "react-i18next";
import { getAccountLogOut, getProfileImage } from '../../services/commonDL';
import { useAppSelector } from '../../store/hooks';
import noImageAvailable from '../../Images/no_image_available.jpg'
import { useNavigate } from 'react-router-dom';
import Swal from 'sweetalert2';
import socalLogo from '../../Images/logo.png';
import { toast } from 'react-toastify';
import PreviewDrawer from '../previewDrawer';
import { Link } from 'react-router-dom';
import { resetApplicationStatus } from '../../store/features/applicationStatusSlice';
import { globalLogoutURL } from '../../services/globalEnv';


const Header: React.FC = () => {
  const navigate = useNavigate();

  const dispatch = useDispatch()
  const bearerToken = useAppSelector((state) => state.authenticate.token);
  const isAadharVerified = useAppSelector((state) => state.authenticate.isAadharVerified);
  const isEmailVerified = useAppSelector((state) => state.authenticate.isEmailVerified);
  const isMobileVerified = useAppSelector((state) => state.authenticate.isMobileVerified);
  const applicationStatus = useAppSelector((state) => state.applicationstatus.savedClickStep);
  const [progressBarNow, setProgressBarNow] = useState(0)
  const isAapaleSarkarLogin = useAppSelector((state) => state.authenticate.isAapaleSarkarLogin);
  const { t } = useTranslation("App.Header");
  const [urlImage, setUrlImage] = useState("")
  const [language, setLanguage] = useState(true)

  const removeAll = () => {
    getAccountLogOut(bearerToken).then((resp) => {
    });
    dispatch(deleteFileUploadState());
    dispatch(resetAllAuthentication());
    dispatch(resetApplicationStatus());
    if (isAapaleSarkarLogin)
      window.location.href = globalLogoutURL;
    else
      navigate("/", { replace: true });
  }

  const handleBasicDetails = () => {
    navigate("/school/register", { replace: true });
  }

  const handleLanguage = () => {
    if (language) {
      setLanguage(false)
      i18n.changeLanguage("en");
    } else {
      setLanguage(true)
      i18n.changeLanguage("mr");
    }

  };

  useEffect(() => {
    // getProfileImage(bearerToken).then(resp => {
    //   let strArray = resp;
    //   let strObj = JSON.parse(strArray);
    //   if (strObj.status === 200 && strObj.status !== undefined) {

    //     if (strObj.data !== undefined) {
    //       setUrlImage(strObj.data.src);
    //     }
    //   }
    //   else if (strObj.status === 401 && strObj.status !== undefined) {
    //     toast(t('UnauthorisedAccessLoggingyouout'), { autoClose: 3000, type: 'error', position: 'top-right' })
    //     navigate('/logout', { replace: true })
    //     return;
    //   }
    // });
  }, [])
  let initialMinutes = 15;
  let initialSeconds = 0;
  const [minutes, setMinutes] = useState(initialMinutes);
  const [seconds, setSeconds] = useState(initialSeconds);
  const [minSecStr, setMinSecStr] = useState({
    minutesStr: "",
    secondsStr: ""
  })

  const [isDrawerOpen, setIsDrawerOpen] = useState(false);

  const handleView = () => {
    setIsDrawerOpen(true);
  };

  useEffect(() => {
    const interval = setInterval(() => {
      if (seconds > 0) {
        setSeconds(seconds - 1);
      }

      if (seconds === 0) {
        if (minutes === 0) {
          clearInterval(interval);
          navigate("/logout", { replace: true });
        } else {
          setSeconds(59);
          setMinutes(minutes - 1);
        }
      }
    }, 1000);

    window.addEventListener('click', resetRemainingTime);
    window.addEventListener('keypress', resetRemainingTime);

    return () => {
      clearInterval(interval);
      window.removeEventListener('click', resetRemainingTime);
      window.removeEventListener('keypress', resetRemainingTime);
    };
  }, [seconds]);

  useEffect(() => {
    resetRemainingTime()
  }, [])

  const resetRemainingTime = () => {
    setMinutes(initialMinutes);
    setSeconds(initialSeconds);
  };

  useEffect(() => {

    if (minutes === 0 && seconds <= 10) {
      let secStrChange = seconds;
      if (!Swal.isVisible()) {
        Swal.fire({
          title: `<strong>Logging you out in </strong><p>${secStrChange} seconds</p>`,
          icon: "info",
          html: `
         Click anywhere to continue
        `,
          focusConfirm: false,
          timer: 10000
        });
      }
      else {
        Swal.update({ title: `<strong>Logging you out in </strong><p>${secStrChange} seconds</p>` })
      }

    }

    let minStr = minutes < 10 ? `0${minutes}` : `${minutes}`;
    let secStr = seconds < 10 ? `0${seconds}` : `${seconds}`;

    setMinSecStr({ minutesStr: minStr, secondsStr: secStr })

  }, [minutes, seconds])

    return (
        <header className="border-bottom d-flex justify-content-start closesub">
          <div  className='socallogowrap d-flex justify-content-start align-items-center px-2'> 
      {/* <img src={socalLogo} alt='' ></img> */}
      Menu
      </div>
      <div className='bg flex-grow-1'>
        <div className="container-fluid  gap-3  ">

          <div className='d-flex w-100 justify-content-between position-relative'>


            <div className='Logo d-none d-md-block'>
              <h1>Raje Yashwantrao Holkar Yojana </h1>
            </div>

            <div className='Logo d-block d-md-none'>
              <h1>Raje Yashwantrao <br /> Holkar Yojana </h1>
            </div>


            <div className="d-flex align-items-start" style={{ marginTop: "-8px" }}>
              <p className='sessiontime'>Session Timeout in {`${minSecStr.minutesStr}:${minSecStr.secondsStr}`}</p>

              <form className="w-100 me-3">
                <input type="button" className="btn btn-outline-light btn-sm rounded-2 " onClick={handleLanguage} value={language === true ? `${t("English")}` : `${t("Marathi")}`} />

              </form>

              <div className="flex-shrink-0 dropdown">
                <a href="#" className="usericon ink-body-emphasis text-decoration-none dropdown-toggle" data-bs-toggle="dropdown" aria-expanded="false">
                  {/* {urlImage === null ? <img src={noImageAvailable} alt="mdo" width="32" height="32" className="rounded-circle noImageAvailable" /> :
                    <img src={urlImage} alt="mdo" width="32" height="32" className="rounded-circle imageAvailable" />
                  } */}
                  <i className="bi bi-person-circle"></i>
                </a>
                <ul className="dropdown-menu text-small shadow mt-1">
                  {/* {(isAadharVerified === true && isEmailVerified === true && isMobileVerified === true) ?
                    (<>
                      <li><a className="dropdown-item" href="#" onClick={handleBasicDetails}>Basic Details</a></li>
                      <li><hr className="dropdown-divider" /></li></>) : (<></>)
                  }

                  {progressBarNow === 100 && (
                    <>
                      <li>
                        <Link className="dropdown-item" onClick={() => handleView()} to={""}>
                          View Profile
                        </Link>
                      </li>
                      <li><hr className="dropdown-divider" /></li>
                    </>
                  )} */}

                  <li><a className="dropdown-item" href="#" onClick={removeAll}>Log out</a></li>
                </ul>
              </div>
            </div>
          </div>
        </div>
      </div>
      <PreviewDrawer
        show={isDrawerOpen}
        onClose={() => setIsDrawerOpen(false)}
        from="Header"
        applicationId=''
        formName=""
      />
    </header>
  );
};

export default Header;
